import React from 'react';
import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';

export default function MealItemForm(props){



  return (
    <form className={classes.form} onSubmit={props.eventHanlder}>
      <Input label="Amount" input={{
        id: 'amount' + props.id,
        type: 'number',
        min: '1',
        max: '5',
        step: '1',
        defaultValue: '1'
      }}/>
      <button onChange={props.eventHanlder}>+ Add</button>
    </form>
  );
}
